create table TRAINSTATION
(
    STATION_ID          VARCHAR2(20) not null
        references STATION,
    TRAIN_ID            VARCHAR2(20) not null
        references TRAIN,
    ARRIVAL_TIME        VARCHAR2(20),
    DEPARTURE_TIME      VARCHAR2(20),
    TRAIN_ENTRANCE      VARCHAR2(20),
    TRAIN_EXIT          VARCHAR2(20),
    DEPART_TRAIN_STATUS VARCHAR2(20),
    ARRIVE_TRAIN_STATUS VARCHAR2(20),
    primary key (STATION_ID, TRAIN_ID)
)
/

